// ============================================
// EXAMPLE 1: Default Configuration (4 Users)
// ============================================

export const example1_default = {
  appName: 'Flint Availability Calendar',
  appShortName: 'FlintCal',
  logoPath: '/FlintCal_Logo.png',

  users: [
    {
      name: 'Flint & Maryam',
      color: '#2196F3',
      displayColor: '#1976D2',
      textColor: '#FFFFFF'
    },
    {
      name: 'Bryan & Marlene',
      color: '#4CAF50',
      displayColor: '#388E3C',
      textColor: '#FFFFFF'
    },
    {
      name: 'Leslie & Manny',
      color: '#FF9800',
      displayColor: '#F57C00',
      textColor: '#FFFFFF'
    },
    {
      name: 'Molly & Jay',
      color: '#9C27B0',
      displayColor: '#7B1FA2',
      textColor: '#FFFFFF'
    }
  ],

  dateRange: {
    startMonth: 10,
    startYear: 2025,
    endMonth: 12,
    endYear: 2025,
    
    getStartDate() {
      return new Date(this.startYear, this.startMonth - 1, 1);
    },
    
    getEndDate() {
      return new Date(this.endYear, this.endMonth, 0);
    },
    
    getDisplayRange() {
      const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 
                     'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
      return `${months[this.startMonth - 1]} ${this.startYear} - ${months[this.endMonth - 1]} ${this.endYear}`;
    }
  },

  ui: {
    theme: 'dark',
    enableMobileOptimizations: true,
    showLoadingIndicators: true,
    animateTransitions: true
  },

  features: {
    realTimeSync: true,
    multiUserSelection: true,
    dateToggle: true,
    monthNavigation: true
  }
};

